class Solution {
    public int maxOperations(String s) {
        int ans=0;
        int cntOne=0;
        boolean hasOne=false;
        List<Integer>bits=new ArrayList<>();
        for(char c:s.toCharArray()){
            if(c=='1') 
            {
                bits.add(1);
                hasOne=true;
            }
            else if(c=='0'&&hasOne){
                bits.add(0);
                hasOne=false;
            }
        }
        for(int bit:bits){
            if(bit==1) cntOne+=1;
            else ans+=cntOne;
        }
        return ans;
    }
}
