class Solution {
    public int maxOperations(String s) {
        int one=0;
        int ans=0;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                one++;
                if(i+1<s.length()&&s.charAt(i+1)=='0'){
                    ans+=one;
                }
            } 
        }
        return ans;
    }
}
