class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int res = 0;
        int count =0;

        for(int i=0;i<n-1;i++){
            if(s[i] == '1' && s[i+1] == '0'){
                res += (count+1);
            } 
            if(s[i] == '1') count++;
        }

        return res;
    }
};
