class Solution {
public:
    int maxOperations(string s) {
        int count1=0, count0=0; 
        int n = s.length();
        if(n==1)
            return 0;
        int windowSize = 0;
        int l=0;int r=0;
        int i=0;
        int found0 = 0;
        while(i<n && s[i]=='0')
            i++;
        l=i;r=i-1;
        count0=0;count1=0;
        long long ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')
                count1++;
            else{
                ans+=count1;
                while(i<n && s[i]=='0')
                    i++;
                i--;
            }
        }
        return ans;
    }
};
