class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        vector<int>prefixsum(n,0);
        if(s[0]=='1') prefixsum[0]=1;
        for(int i=1;i<n;i++){
            if(s[i]=='1'){
                prefixsum[i]=1+prefixsum[i-1];
            }else{
                prefixsum[i]=prefixsum[i-1];
            }
        }
        for(int i=0;i<n-1;i++){
            if(s[i]=='1' && s[i+1]=='0'){
                ans+=prefixsum[i];
            }
        }
        return ans;
    }
};
