class Solution:
    def maxOperations(self, s: str) -> int:
        res = []
        for i, c in enumerate(s):
            if c == '1':
                res.append(i)
        ans = 0
        if s[-1] == '0':
            res.append(len(s))
        for i in range(len(res) - 1):
            if res[i + 1] - res[i] > 1:
                ans += (i + 1)
        return ans
