class Solution:
    def maxOperations(self, s: str) -> int:
        count = 0
        res = 0
        prev_zero = False
        for i in range(len(s)):
            if s[i] == '1':
                count += 1
                prev_zero = False
            if s[i] == '0' and not prev_zero:
                res += count
                prev_zero = True
        return res
