class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        groups = []
        group_count = 0
        for i in range(n): # NOTE 2: Got this as `range(n + 1)` at first.
            if s[i] == '1':
                group_count += 1
            else:
                if group_count:
                    groups.append(group_count)
                group_count = 0 # NOTE 1: Didn't have this line.
        groups = groups[::-1]
        answer = 0
        while groups:
            top = groups.pop()
            answer += top
            if groups:
                groups[-1] += top
        return answer
