class Solution {
public:
    int maxOperations(string s) {
        int streak=1;
        int curr=0;
        int ans=0;
        for(int i=0;i<s.length()-1;i++){
            if(s[i]=='1' && s[i+1]=='0'){
                curr+=streak;
                streak=1;
            } else if(s[i]=='1' && s[i+1]=='1'){
                streak++;
            } else if (s[i]=='0' && s[i+1]=='1'){
                ans+=curr;
            }
        }
        if (s.back()=='0'){
            ans+=curr;
        }
        return ans;
    }
};
