class Solution {
public:
    int maxOperations(string s) {

        int n=s.size();

        vector<int>nums(n,0);

        if(s[0]=='1') nums[0]++;

        for(int i=1;i<n;i++){

            if(s[i]=='1' ) nums[i]=1+nums[i-1];

            else nums[i]=nums[i-1];

        }

        int ans=0;

        int i=0;
        while(i<n){
            if(s[i]=='0'){
                ans+=nums[i];

                while(s[i]=='0' && i<n){
                    i++;
                }
            }
            else{
                i++;
            }
        }

        return ans;

    }
};
