class Solution {
public:
    int maxOperations(string s) 
    {
        int ans=0;
        bool flag=true;
        for(int i=0,curr=0;i<s.size();i++)
        {
            if(s[i]=='0')
            {
                if(flag)
                {
                    ans+=curr;
                    flag=false;
                }
            }
            else
            {
                curr++;
                flag=true;
            }
        }
        return ans;
    }
};
