class Solution {
public:
    int maxOperations(string s) {
        int mxOp = 0;
        int n = s.size();

        int ind = 0;
        int temp = 0;

        while(ind < n){
           // int cnt = 0;

            while(ind < n && s[ind] == '1'){
               // cnt++;
                ind++;
                temp++;
            }

            if(ind < n && s[ind] == '0'){
                while(ind  < n && s[ind] == '0'){
                    ind++;
                }

                mxOp += (temp);
            }

        }

        return mxOp;
    }
};
