function maxOperations(s: string): number {
  // 1 0 11 0 11 0 11 0 1
  // 0    1    2    3
  const ss = [...s]
  const n = ss.length
  let i = 0
  // 多少个1
  const a = []
  while (i < n) {
    if (ss[i] === '0') {
      i++
      continue
    }
    let start = i
    while (i < n && ss[i] === '1') {
      i++
    }
    // [start, i-1] 是一组
    // [i, ..] 下一组
    if (i !== n && i > start) {
      a.push(i - start)
    }
  }
  // console.log('--->', a)
  let ans = 0
  const len = a.length
  for (let i = 0; i < len; i++) {
    ans += (len - i) * a[i]
  }
  return ans
}
