class Solution:
    def maxOperations(self, s: str) -> int:
        cur = s[0]=="1"
        c = int(cur)
        a = 0
        for i in range(1,len(s)):
            if cur:
                if s[i] == "1":
                    c += 1
                else:
                    a += c
                    cur = False
            else:
                if s[i] == "1":
                    c+=1
                    cur = True
        return a
