class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        vector<int> pre(n);
        for(int i=0;i<n;i++){
            if(i==0 && s[i]=='1') pre[i]=1;
            else if(i==0 && s[i]=='0') pre[i]=0;
            else if(i>0 && s[i]=='1') pre[i] = pre[i-1]+1;
            else pre[i]=pre[i-1];
        }
        int ans=0;
        for(int i=0;i<n-1;i++){
            //cout<<pre[i]<<" ";
            if(s[i]=='1'&& s[i+1]=='0') ans += pre[i];
        }
        return ans;
    }
};
