class Solution {
public:
    int maxOperations(string s) {
        int sol = 0, ct = 0, n = s.size(), oct = count(s.begin(),s.end(),'1');
        for (int i = 0; i < n; ++i) {
            if (s[i] == '0') continue;
            --oct;
            if (i == n-oct-1) continue;
            ct++;
            if (i != n-1 && s[i+1] == '1') continue;
            sol += ct;
        }
        return sol;
    }
};
