class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt1=0;
        bool check=false;
        for(int i=0;i<s.length();i++){
            char ch=s[i];
            if(ch=='1'){
                cnt1++;
                check=true;
            }
            else if(ch=='0' && check){
                ans+=cnt1;
                check=false;
            }
        }
        return ans;
    }
};
