class Solution {
public:
    int maxOperations(string s) {
        int n=s.length(),i,j,ans=0,curr=0;
        for(i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                curr++;
                if(i+1<n && s[i+1]=='0')
                {
                    for(j=i+2;j<n;j++)
                    {
                        if(s[j]=='1')
                        break;
                    }
                    i=j-1;
                    ans+=curr;
                }
            }
        }
        return ans;
    }
};
