class Solution {
public:
    int maxOperations(string s) {
        int result = 0;
        int cnt = 0;
        bool flag = false;
        for (char c : s) {
            if (c == '1') {
                cnt++; 
                flag = true;
            }
            else if (c == '0' && flag) {
                result += cnt;  
                flag = false;  
            }
        }

        return result;
    }
};
