class Solution {
public:
    int maxOperations(string s) {
        vector<int> dp(s.size() + 1, 0);
        int ans = 0, zeroGroups = 0, prev = -1;
        for(int i = s.size() -1; i >= 0; i--) {
            if(s[i] == '0') {
                if(prev != '0')
                zeroGroups++;
            } else {
                ans += zeroGroups;
            }

            prev = s[i];
        }
        return ans;
    }
};
