class Solution {
  int maxOperations(String s) {
    int res = 0;
    int zeros = 0;
    bool zero = false;

    for (int i = s.length - 1; i >= 0; i--){
      if (!zero && s[i] == '0'){
        zeros++;
        zero = true;
        continue;
      }
      if (s[i] == '1'){
        res += zeros;
        zero = false;
      }
    }
    return res;
  }
}
