class Solution:
    def maxOperations(self, s: str) -> int:

        answer = 0 
        stack = []
        for i, char in enumerate(s): 
            if i > 0 and  s[i-1] == '0' and s[i] == '1': 
                answer += len(stack)
            if char == '1' : 
                stack.append('1')

        if s[-1] == '0': 
                answer += len(stack)


        return answer
