class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int z=0,o=0;
        int ans=0;
        bool flag=true;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0' && flag){
                z++;
                flag=false;
            }
            if(s[i]=='1'){
                flag=true;
                ans+=z;
            }
        }
        return ans;
    }
};
