class Solution {
public:
    int maxOperations(string s) {
        int n=size(s)+1;
        s+='1';
        int l=0,r=0,cnt=0,ans=0;
        while(r<n){
            while(r<n && s[r]=='1'){
                r++;
                cnt++;
            }
            while(r<n && s[r]=='0') r++;
            ans+=cnt;
        }
        return ans-count(begin(s),end(s),'1');

    }
};
