class Solution {
public:
    int maxOperations(string s) {
        int res = 0, cone = 0;
        for (int i = 0; i < s.size();) {
            if (s[i] == '0') {
                while (i < s.size() && s[i] == '0') {
                    i++;
                }
                res += cone;
            } else {
                i++;
                cone++;
            }
        }

        return res;
    }
};
