class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        boolean pos = false;
        int ans = 0;
        for(int i=0;i<s.length();i++) {
            if(s.charAt(i) == '1') {
                if(i>1 && s.charAt(i-1) == '0')
                    ans += cnt;
                cnt++;
            }
        }
        if(s.charAt(s.length()-1) == '0')
            ans += cnt;
        return ans;
    }
}
