class Solution:
    def maxOperations(self, s: str) -> int:
        a, n = [(0 if a == '0' else 1) for a in list(s)], len(s)
        i = 0
        count = 0
        count1s = 0
        while i < n:
            if (a[i] == 0):
                start, end = i, i
                while (end < n and a[end] == 0):
                    end += 1
                count += count1s
                i = end
            else:
                count1s += 1
                i += 1
            # print("i, count1s = ",i, count1s)
        # print("*"*10)
        return count
