class Solution {
public:
    int maxOperations(string s) {
            ll n = s.length();
            ll idx = 0;
            ll extra = 0;
            int ans = 0;
            while(idx < n){
                if(s[idx]=='0'){
                idx += 1;
                continue;
                }
                ll curr = 0;
                while(s[idx] == '1'){
                idx+=1;
                curr += 1;
                }
                if(s[idx]=='0'){
                    ans += curr + extra;
                    extra += curr;
                }
            }
            return ans;
    }
};
