const int N = 2e5 + 10; // check the limits


class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        for (int i = 0, j; i < sz(s); ) {
            if (s[i] == '0') {
                for (j = i; j < sz(s) && s[j] == '0'; j++);
                ans += cnt;
                i = j;
            } else {
                cnt++;
                i++;
            }
        }
        return ans;
    }
};
