class Solution:
    def maxOperations(self, s: str) -> int:
        dp = [0] * len(s)
        ones = [0] * len(s)
        if s[0] == '1':
            ones[0] = 1
        for i in range(1, len(s)):
            ones[i] = ones[i - 1] + (s[i] == '1')
        cur = 0
        if s[0] == '1':
            cur += 1
        for i in range(1, len(s)):
            if s[i] == '0':
                if cur:
                    dp[i] = dp[i - 1] + ones[i - 1]
                    cur = 0
                else:
                    dp[i] = dp[i - 1]
            else:
                dp[i] = dp[i - 1]
                cur += 1
        return dp[-1]
