class Solution {
public:
    int maxOperations(string s) {
        int ans=0,on=0;
        bool flag=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                on++;
                flag=true;
            }
            else{
                if(flag)
                    ans+=on;
                flag=false;
            }
        }
        return ans;
    }
};
