class Solution {
    public int maxOperations(String s) {
        int ans=0,c1=0;

        int i=0;

        while(i<s.length()){
            char c=s.charAt(i);
            if(c=='1'){
                c1++;
                i++;
            } 
            else {

            ans+=c1;
            while(i<s.length() && s.charAt(i)=='0'){
                i++;
            }
            }
        }

        return ans;
    }
}
