func maxOperations(s string) (ans int) {
    stk := []int{}
    for i, r := range s {
        switch {
        case r != '1':
        case i > 0 && s[i-1] == '1':
            stk[len(stk)-1] += 1
        default:
            stk = append(stk, 1)
        }
    }
    for len(stk) > 1 {
        ans += stk[0]
        stk[1] += stk[0]
        stk = stk[1:]
    }
    if len(stk) > 0 && s[len(s)-1] != '1' {
        ans += stk[0]
    }
    return ans
}
