class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), c1 = 0, prev = 0, ans = 0;
        for(int i = 0; i < n; i++) {
            c1 += s[i] == '1';
            int cur = s[i] - '0';
            if(prev == 1 && cur == 0) ans += c1;
            prev = cur;
        }
        return ans;
    }
};
