class Solution {
public:
    int maxOperations(string s) {
        int c=0;
        for(int i=0;i<s.length()-1;i++){
           if(s[i]=='0' && s[i+1]=='0'){
                s[i]='#';
           }
        }
        int ans=0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1'){
                c++;
            }
            if(s[i]=='0'){
                ans=ans+c;
            }
        }
        return ans;
    }
};
