class Solution {
public:
    int maxOperations(string s) {
        s+='1';
        int n=s.size();
        int result=0;
        int count=0;
        int pre=1;
        for(int i=n-1;i>-1;i--){
            if(s[i]=='0'){
                if (pre!=0){
                    count++;
                }
                pre=0;
            }
            else{
                pre=1;
                result+=count;
            }
        }
        return result;
    }
};
