class Solution {
public:
    int maxOperations(string s) {
        int prv = 0;
        int ans = 0;
        for (int i = s.size() - 1; i >= 0; i--) {
            if (s[i] == '1') {
                ans += prv;
                if (i != s.size() - 1 && s[i + 1] == '0') {
                    ans++;
                    prv++;
                }

            }

        }
        return ans;
    }
};
