class Solution {
public:
    int maxOperations(string s) {
    	int idx = s.size(), last = 0, ans = 0;
        for(int i = s.size() - 1; i >= 0; i -- )
        {
        	if(s[i] == '1')
        	{
        		int t = idx - i;
        		if(t >= 2) last ++ ;
        		ans += last;
        		idx = i;
			}
		}
		return ans;
    }
};
