/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let n = s.length;
    let clusters = 0;
    let res = 0;
    for(let i=n-1;i>=0;i--) {
        if(s[i]==='0') clusters++;
        while(s[i]==='0' && i>=0) i--;
        if(s[i]==='1')  res+=clusters;
    }
    return res;
};
