class Solution {
public:
    int maxOperations(string s) {
        int i,j;

        int k=0,ans=0;;


        for(i=0;i<size(s);i++){




            if(k>0 && s[i]=='1' && s[i-1]=='0'){

                    ans=ans+k;
                }






            if(s[i]=='1'){

                k++;
                //continue;
            }






        }

        int n=s.size();

        if(s[n-1]=='0') ans=ans+k;

        return ans;
    }
};
