class Solution(object):
    def maxOperations(self, s):
        i=len(s)-1
        c=0
        ans=0
        p=0
        while(i>=0):
            while(i>=0 and s[i]=='1'):
                p+=1
                i-=1
            ans+=p*c
            if(i>=0 and s[i]=='0'):
                p=0
                c+=1
            while(i>=0 and s[i]=='0'):
                i-=1


        return ans
