class Solution {
    public int maxOperations(String s) {
        int cntOne = 0;
        int i = 0;
        int n = s.length();
        int ans = 0;
        boolean o = false;
        boolean z = false;
        while (i < n) {
            while (i < n && s.charAt(i) == '1') {
                cntOne++;
                i++;
                o = true;
            }
            while (i < n && s.charAt(i) == '0') {
                i++;
                z = true;
            }
            if (o && z)
                ans += cntOne;
            o = false;
            z = false;
        }
        return ans;
    }
}
