class Solution {
public:
    int maxOperations(string s) {
        int ct=0,ct2=0;
        int n=s.size();
        vector<int> dp(n,0);
        int i;
        for(i=n-1;i>=0;i--)
        {
            if(s[i]=='0' && ct==0)
            {
                ct2++;
                while(i>=0 && s[i]=='0') i--;
                break;
            }
        }
        while(i>=0)
        {
            if(s[i]=='1')
            {
                dp[i]=ct2;
                i--;
            }
            else{
                ct2++;
                while(i>=0 && s[i]=='0') i--;
            }
        }
        int ans=0;
        for(int i=0;i<n;i++) ans+=dp[i];
        return ans;
    }
};
