class Solution:
    def maxOperations(self, s: str) -> int:
        bits = s
        n = len(bits)
        result = 0
        ones = 0
        ones_previous = 0
        for index in range(n):
            if bits[index] == "1":
                ones += 1 + ones_previous
                ones_previous = 0
            else:
                result += ones
                ones_previous += ones
                ones = 0

        return result
