class Solution {
public:
    int maxOperations(string s) {
        vector<int> v;
        int i = 0;
        while(i < s.length()){
            if(s[i] == '0'){
                i++;
            }else{
                int j = i;
                while(j < s.length() and s[j] == '1'){
                    j++;
                }
                v.push_back(j-i);
                i = j;
            }
        }
        if(v.size() == 0){
            return 0;
        }
        int ans = 0;
        int pref_sum = 0;
        for(auto k: v){
            pref_sum += k;
            ans += pref_sum;
        }
        if(s[s.length()-1] == '1'){
            ans -= pref_sum;
        }
        return ans;
    }
};
