class Solution {
public:
    int maxOperations(string s) {
        int ans=0,count=0,n=s.length();
        bool flag=false;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                count++;
                flag=true;
            }
            else
            {
                if(flag)
                {
                    ans+=count;
                    flag=!flag;
                }
            }
        }
        return ans;
    }
};
