class Solution {
public:
    int maxOperations(string s) {
         int ans=0;
         int count=0;
        bool matchString=true;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                count+=1;
                matchString=true;
            }
            else{
                if(matchString !=false){
                ans=ans+count;

                matchString=false;
                }
            }
        }
        return ans;
    }
};
