class Solution:
    def maxOperations(self, s: str) -> int:
        total = s.count("1")
        left = total
        result = 0
        is_zero_before = False
        while s:
            if s[-1] == "0":
                if not is_zero_before:
                    result += left
                is_zero_before = True
            if s[-1] == "1":
                left -= 1
                is_zero_before = False
            s = s[:-1]
        return result
