class Solution {
    public int maxOperations(String s) {
        int oprns = 0, ones = 0;
        for (int idx=0; idx<s.length(); idx++) {
            if (s.charAt(idx)=='1') {
                ones++;
            } else if (idx>0 && s.charAt(idx-1)=='1') {
                oprns += ones;
            }
        }
        return oprns;
    }
}
