class Solution {
public:
    int maxOperations(string s) {

        int cnt = 0;
        int n = s.length();



        int z = 0;

        int ans = 0;

        for(int i=0; i<n; ++i)
        {
            char ch = s[i];

            if(ch == '0')
                ++z;

            else
            {
                if(z!=0)
                {
                    ans+=cnt;
                }        

                z = 0;
                ++cnt;
            }
        }

        if(z != 0)
            ans += cnt;


        return ans;




    }
};
